import React, { useState, useEffect } from 'react';
import Board from './Board';

const App = () => {
  const [board, setBoard] = useState(Array(9).fill(null));
  const [isXTurn, setIsXTurn] = useState(true);
  const [gameOver, setGameOver] = useState(false);
  const [winner, setWinner] = useState(null);

  const lines = [
    [0, 1, 2], [3, 4, 5], [6, 7, 8],
    [0, 3, 6], [1, 4, 7], [2, 5, 8],
    [0, 4, 8], [2, 4, 6],
  ];

  const checkWinner = (board) => {
    for (let [a, b, c] of lines) {
      if (board[a] && board[a] === board[b] && board[b] === board[c]) {
        return board[a];
      }
    }
    if (board.every(cell => cell)) return 'Draw';
    return null;
  };

  const handleClick = (index) => {
    if (board[index] || gameOver || !isXTurn) return;
    const newBoard = [...board];
    newBoard[index] = 'X';
    setBoard(newBoard);
    setIsXTurn(false);
  };

  const makeComputerMove = () => {
    const emptyIndices = board
      .map((cell, idx) => (cell === null ? idx : null))
      .filter(idx => idx !== null);

    if (emptyIndices.length === 0 || gameOver) return;

    const randomIndex = emptyIndices[Math.floor(Math.random() * emptyIndices.length)];
    const newBoard = [...board];
    newBoard[randomIndex] = 'O';
    setTimeout(() => {
      setBoard(newBoard);
      setIsXTurn(true);
    }, 500);
  };

  useEffect(() => {
    const result = checkWinner(board);
    if (result) {
      setWinner(result);
      setGameOver(true);
    } else if (!isXTurn) {
      makeComputerMove();
    }
  }, [board, isXTurn]);

  const resetGame = () => {
    setBoard(Array(9).fill(null));
    setIsXTurn(true);
    setGameOver(false);
    setWinner(null);
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>Tic Tac Toe - Single Player</h1>
      <Board squares={board} onClick={handleClick} />
      {winner && <h2>{winner === 'Draw' ? 'It\'s a draw!' : `Winner: ${winner}`}</h2>}
      {gameOver && <button onClick={resetGame}>Restart</button>}
    </div>
  );
};

export default App;
