import { useState } from "react";
import "./App.scss";

function App() {
  const initialData = [
    ["", "", ""],
    ["", "", ""],
    ["", "", ""],
  ];
  const [tern, setTern] = useState(0);
  const [datas, setData] = useState(initialData);

  const changeData = (x, y) => {
    if (datas[x][y] === "") {
      datas[x][y] = tern;
      setData(datas);
      setTern(tern == 0 ? 1 : 0);
    }
  };

  // const checkWin = () => {
  //   let ADatas, BDatas;
  //   for (x in datas) {
  //     for (y in datas[x]) {
  //       if (datas[x][y] === 0) {
  //         ADatas.push([x, y]);
  //       } else if (datas[x][y] === 0) {
  //         BDatas.push([x, y]);
  //       }ADatas
  //     }
  //   }
  // };

  return (
    <>
      <div class="container">
        <h1>Tic Tac Toe</h1>
        <div>
          <table>
            <tbody>
              {datas.map((row, x) => (
                <tr key={x}>
                  {row.map((col, y) => (
                    <td key={y} onClick={() => changeData(x, y)}>
                      <p>{col === 0 ? "O" : col === 1 ? "X" : ""}</p>
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div>
          <button onClick={() => setData(initialData)}>reset</button>
          <p onClick={() => setTern(tern == 0 ? 1 : 0)}>{tern}</p>
        </div>
      </div>
    </>
  );
}

export default App;
