import { useState } from "react";
import "./App.scss";

function App() {
  const initialData = [
    ["", "", ""],
    ["", "", ""],
    ["", "", ""],
  ];
  const [tern, setTern] = useState(0);
  const [datas, setData] = useState(initialData);

  const changeData = (x, y) => {
    if (datas[x][y] === "") {
      datas[x][y] = tern;
      setData(datas);
      setTern(tern === 0 ? 1 : 0);
      checkWin();
    }
  };

  const checkWin = () => {
    let XDatas = [],
      YDatas = [];
    for (let x in datas) {
      for (let y in datas[x]) {
        if (datas[x][y] === 0) {
          XDatas.push([x, y]);
          // XDatas["x"] = XDatas["x"] ? XDatas["x"] + 1 : 1;
          // XDatas["y"] = XDatas["y"] ? XDatas["y"] + 1 : 1;
        } else if (datas[x][y] === 1) {
          YDatas.push([x, y]);
        }
      }
    }
    // for (let x in XDatas) {
    //   if (x > 0 && XDatas) {
    //   }
    // }
    console.log(XDatas, YDatas);
    console.log(XDatas.every((v, i) => i > 0 && v[i] === v[-1]));
  };

  return (
    <>
      <div className="container">
        <h1>Tic Tac Toe</h1>
        <div>
          <table>
            <tbody>
              {datas.map((row, x) => (
                <tr key={x}>
                  {row.map((col, y) => (
                    <td key={y} onClick={() => changeData(x, y)}>
                      <p>{col === 0 ? "O" : col === 1 ? "X" : ""}</p>
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div>
          <button onClick={() => setData(initialData)}>reset</button>
          <p onClick={() => setTern(tern === 0 ? 1 : 0)}>{tern}</p>
        </div>
      </div>
    </>
  );
}

export default App;
