import { useEffect, useState } from "react";
import "./App.scss";

function App() {
  const noRows = 3;
  const initialData = [...Array(noRows).keys()].map(() => [
    ...Array(noRows).fill(null),
  ]);
  const [tern, setTern] = useState(0);
  const [boardData, setBoardData] = useState(initialData);
  const [soloPlay, setSoloPlay] = useState(true);
  const [gameOver, setGameOver] = useState(false);
  const [winner, setWinner] = useState(true);

  useEffect(() => {
    const winner = checkWinner(boardData);
    if (winner) {
      setWinner(winner);
      setGameOver(true);
    } else if (soloPlay) {
      computerMove();
    }
  }, [boardData]);
  // Try to win
  const changeData = (x, y) => {
    const newBoard = [...boardData];
    if (newBoard[x][y] === null) {
      newBoard[x][y] = tern;
      setBoardData(newBoard);
      setTern(tern === 0 ? 1 : 0);
    }
  };

  const computerMove = () => {
    const board = structuredClone(boardData);

    let player = tern;
    for (let x in boardData) {
      for (let y in boardData[x]) {
        const boardCopy = structuredClone(board);
        if (boardCopy[x][y] === null) {
          boardCopy[x][y] = player;

          if (checkWinner(boardCopy) === player) {
            makeMove(x, y);
            return;
          }
        }
      }
    }

    player = tern ? 0 : 1;
    // Block player win
    for (let x in boardData) {
      for (let y in boardData[x]) {
        const boardCopy = structuredClone(board);

        if (boardCopy[x][y] == null) {
          boardCopy[x][y] = player;

          if (checkWinner(boardCopy) === player) {
            makeMove(x, y);
            return;
          }
        }
      }
    }

    // Take center
    let center = Math.floor(noRows / 2);
    if (board[center][center] === null) {
      makeMove(center, center);
      return;
    }

    // Take a corner
    let corners = [
      [0, 0],
      [0, noRows - 1],
      [noRows - 1, 0],
      [noRows - 1, noRows - 1],
    ];
    for (let [x, y] of corners) {
      if (board[x][y] === null) {
        makeMove(x, y);
        return;
      }
    }

    // Take sides
    for (let x in board) {
      for (let y in board[x]) {
        if (board[x][y] === null) {
          makeMove(x, y);
          return;
        }
      }
    }
  };

  const makeMove = (x, y) => {
    const newBoard = [...boardData];
    if (newBoard[x][y] === null) {
      newBoard[x][y] = tern ? 0 : 1;
      setBoardData(newBoard);
      setTern(tern === 0 ? 1 : 0);
    }
  };

  const checkWinner = (board) => {
    if (board.every((cell) => cell >= 0)) return "Draw";
    let tempDatas = {};
    for (let x in board) {
      for (let y in board[x]) {
        let player = board[x][y] === 0 ? 0 : board[x][y] === 1 ? 1 : null;
        if (player >= 0) {
          if (!tempDatas[player]) {
            tempDatas[player] = { x: {}, y: {} };
          }
          tempDatas[player]["x"][x] = tempDatas[player]["x"][x]
            ? tempDatas[player]["x"][x] + 1
            : 1;
          tempDatas[player]["y"][y] = tempDatas[player]["y"][y]
            ? tempDatas[player]["y"][y] + 1
            : 1;

          if (
            tempDatas[player]["x"][x] == noRows ||
            tempDatas[player]["y"][y] == noRows
          ) {
            return player;
          } else {
            let diagonalX = Object.keys(tempDatas[player]["x"]).length;
            let diagonalY = Object.keys(tempDatas[player]["y"]).length;
            if (diagonalX == noRows && diagonalY == noRows) {
              return player;
            }
          }
        }
      }
    }
    return null;
  };

  return (
    <>
      <div className="container">
        <h1>Tic Tac Toe</h1>
        <div>
          <table>
            <tbody>
              {boardData.map((row, x) => (
                <tr key={x}>
                  {row.map((col, y) => (
                    <td key={y} onClick={() => changeData(x, y)}>
                      <p>{col === 0 ? "O" : col === 1 ? "X" : ""}</p>
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div>
          <button onClick={() => setBoardData(initialData)}>reset</button>
          <p onClick={() => setTern(tern === 0 ? 1 : 0)}>{tern}</p>
        </div>
      </div>
    </>
  );
}

export default App;
