import { useState } from "react";
import "./App.scss";

function App() {
  const noRows = 3;
  const initialData = [...Array(noRows).keys()].map(() => [
    ...Array(noRows).fill(""),
  ]);
  const [tern, setTern] = useState(0);
  const [boardDatas, setData] = useState(initialData);

  const changeData = (x, y) => {
    if (boardDatas[x][y] === "") {
      boardDatas[x][y] = tern;
      setData(boardDatas);
      setTern(tern === 0 ? 1 : 0);
      checkWin();
    }
  };

  const getHint = (data) => {
    for (let i of [...Array(noRows).keys()]) {
      if (!data[i]) {
        return i;
      }
    }
    return "";
  };

  const checkWin = () => {
    let tempDatas = {};
    for (let x in boardDatas) {
      for (let y in boardDatas[x]) {
        let player =
          boardDatas[x][y] === 0 ? "O" : boardDatas[x][y] === 1 ? "X" : "";
        if (player) {
          if (!tempDatas[player]) {
            tempDatas[player] = { x: {}, y: {} };
          }
          tempDatas[player]["x"][x] = tempDatas[player]["x"][x]
            ? tempDatas[player]["x"][x] + 1
            : 1;
          tempDatas[player]["y"][y] = tempDatas[player]["y"][y]
            ? tempDatas[player]["y"][y] + 1
            : 1;

          if (
            tempDatas[player]["x"][x] == noRows ||
            tempDatas[player]["y"][y] == noRows
          ) {
            console.log(player, "win");
          } else if(tempDatas[player]["x"][x] == noRows - 1 || tempDatas[player]["y"][y] == noRows - 1) {
            if (tempDatas[player]["x"][x] == noRows - 1) {
              console.log(player, "hintx", x, getHint(tempDatas[player]["y"]));
            }
            if (tempDatas[player]["y"][y] == noRows - 1) {
              console.log(player, "hinty", getHint(tempDatas[player]["x"]), y, tempDatas[player]["x"],tempDatas[player]["y"]);
            }
          } 

          let diagonalX = Object.keys(tempDatas[player]["x"]).length;
          let diagonalY = Object.keys(tempDatas[player]["y"]).length;
          if (diagonalX == noRows && diagonalY == noRows) {
            console.log(player, "win");
          } else if (diagonalX == noRows - 1 && diagonalY == noRows - 1) {
            console.log(player, "available win");
          }

        }
        // if (player) {
        //   if (!tempDatas[player]) tempDatas[player] = [];
        //   tempDatas[player].push([x, y]);
        // }

        // if (boardDatas[x][y] === 0) {
        //   tempDatas.push([x, y]);
        // } else if (boardDatas[x][y] === 1) {
        //   YDatas.push([x, y]);
        // }
      }
    }
    // console.log(tempDatas);
    // for (let [player, playerData] of Object.entries(tempDatas)) {
    //   for (let axis in playerData) {
    //     axis.reduce((v, t) => v[0] == t, false);
    //     if (axis > 0) {
    //       if (player[axis][0] == player[axis - 1][0]) {
    //       } else if (player[axis][0] == player[axis - 1][0]) {
    //       }
    //     }
    //   }
    // }
    // for (let [player, playerData] of Object.entries(tempDatas)) {
    //   for (let [axis, axisData] of Object.entries(playerData)) {
    //     for (let key in axisData) {
    //       if (axisData[key] == 3) {
    //         console.log(player, "win");
    //       } else if (axisData[key] == 2) {
    //         console.log(player, "available win");
    //       }
    //     }
    //   }
    // }
  };

  return (
    <>
      <div className="container">
        <h1>Tic Tac Toe</h1>
        <div>
          <table>
            <tbody>
              {boardDatas.map((row, x) => (
                <tr key={x}>
                  {row.map((col, y) => (
                    <td key={y} onClick={() => changeData(x, y)}>
                      <p>{col === 0 ? "O" : col === 1 ? "X" : ""}</p>
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div>
          <button onClick={() => setData(initialData)}>reset</button>
          <p onClick={() => setTern(tern === 0 ? 1 : 0)}>{tern}</p>
        </div>
      </div>
    </>
  );
}

export default App;
